from django.db import models
from django.utils.timezone import now
from datetime import timedelta


class Membre(models.Model):
    nom = models.CharField(max_length=100)
    email = models.EmailField(unique=True)
    date_inscription = models.DateField(auto_now_add=True)

    def nombre_emprunts_actifs(self):
        return Emprunt.objects.filter(membre=self, rendu=False).count()

    def a_emprunts_en_retard(self):
        """ Vérifie si le membre a des emprunts en retard """
        return Emprunt.objects.filter(membre=self, date_retour_prevu__lt=now().date(), rendu=False).exists()
    
    def est_bloque(self):
        """ Vérifie si le membre a un emprunt en retard """
        return self.a_emprunts_en_retard()

    def __str__(self):
        return f"{self.nom} ({self.email})"


class Media(models.Model):
    TYPE_CHOICES = [
        ('livre', 'Livre'),
        ('dvd', 'DVD'),
        ('cd', 'CD'),
        ('jeu', 'Jeu de plateau'),
    ]
    titre = models.CharField(max_length=255)
    type = models.CharField(max_length=10, choices=TYPE_CHOICES)
    disponible = models.BooleanField(default=True)
    empruntable = models.BooleanField(default=True)
    quantite_disponible = models.IntegerField(default=1)

    auteur = models.CharField(max_length=255, blank=True, null=True)
    realisateur = models.CharField(max_length=255, blank=True, null=True)
    artiste = models.CharField(max_length=255, blank=True, null=True)

    def save(self, *args, **kwargs):
        if self.type == 'jeu':
            self.empruntable = False
        self.disponible = self.quantite_disponible > 0
        super().save(*args, **kwargs)

    def est_disponible(self):
        """Vérifie si le média est encore en stock."""
        return self.quantite_disponible > 0

    def __str__(self):
        return f"{self.titre} ({self.get_type_display()}) - Stock: {self.quantite_disponible}"


def default_date_retour():
    return now().date() + timedelta(days=14)


class Emprunt(models.Model):
    membre = models.ForeignKey(Membre, on_delete=models.CASCADE)
    media = models.ForeignKey(Media, on_delete=models.CASCADE)
    date_emprunt = models.DateField()
    date_retour_prevu = models.DateField(blank=True, null=True)
    rendu = models.BooleanField(default=False)

    def save(self, *args, **kwargs):
        """Vérification avant de sauvegarder l'emprunt"""
        if not self.pk:  # Si c'est un nouvel emprunt
            if self.membre.a_emprunts_en_retard():
                raise ValueError("Ce membre a des emprunts en retard et ne peut pas emprunter !")

            if self.media.quantite_disponible > 0:
                self.media.quantite_disponible -= 1
                self.media.save()
            else:
                raise ValueError(f"Le média '{self.media.titre}' n'est plus disponible en stock !")

        if not self.date_retour_prevu:
            self.date_retour_prevu = self.date_emprunt + timedelta(days=7)

        super().save(*args, **kwargs)

    def delete(self, *args, **kwargs):
        """ Augmente le stock lors du retour """
        if not self.rendu:
            self.media.quantite_disponible += 1
            self.media.save()
            self.rendu = True  # Empêcher un deuxième ajout du stock si l'objet est supprimé une deuxième fois

        super().delete(*args, **kwargs)

    def en_retard(self):
        """Vérifie si l'emprunt est en retard."""
        return not self.rendu and now().date() > self.date_retour_prevu

    def __str__(self):
        retard = " (EN RETARD)" if self.en_retard() else ""
